-- Actualizar secuencia
SELECT setval('orders_id_seq', (SELECT MAX(id) FROM orders));

-- Mostrar resumen
SELECT 
    status,
    COUNT(*) as total,
    ROUND(SUM(estimated_production_time), 2) as total_hours
FROM orders
GROUP BY status
ORDER BY 
    CASE status
        WHEN 'in_progress' THEN 1
        WHEN 'pending' THEN 2
        WHEN 'completed' THEN 3
        WHEN 'delivered' THEN 4
    END;
